plugin.phpcode:
$vbulletin->input->clean_array_gpc('r', array(
  'closed' => TYPE_STR,
));

// default value (for excluded forums too)
$filterclosedthreads =
  $foruminfo['forumid']
    ? intval($foruminfo['fct_hide_by_default'])
    : 0;

if ($foruminfo['forumid'] AND $foruminfo['fct_allow_user_set'])
{
  // try to get saved state
  if ($vbulletin->userinfo['userid'])
  {
    // unserialise filters array
    if (($fct_data = unserialize($vbulletin->userinfo['fct_data'])) === false)
        $fct_data = array();

    // get saved or default value
    $filterclosedthreads = array_key_exists("$foruminfo[forumid]", $fct_data) ? $fct_data["$foruminfo[forumid]"] : $foruminfo['fct_hide_by_default'];
  }

  if (!empty($vbulletin->GPC['closed']))
  {
    $fct_new_state = $vbulletin->GPC['closed'] == 'hide' ? 1 : 0;
    if ($fct_new_state != $filterclosedthreads)
    {
      // update saved state
      $filterclosedthreads = $fct_new_state;

      // Save state only for registered users
      if ($vbulletin->userinfo['userid'])
      {
        if ($fct_new_state <> intval($foruminfo['fct_hide_by_default']))
        {
          $fct_data["$foruminfo[forumid]"] = $fct_new_state;
        }
        else {
          unset($fct_data["$foruminfo[forumid]"]);
        }

        // init user data manager
        $fct_userdata =& datamanager_init('User', $vbulletin, ERRTYPE_ARRAY);
        $fct_userdata->set_existing($vbulletin->userinfo);
        $fct_userdata->set('fct_data', $fct_data);
        $fct_userdata->save();

        unset($fct_userdata);
      }
    }
    unset($fct_new_state);
  }
}

$fct_search  = array(
    '<td class=\"smallfont\" colspan=\"2\">'
  );

$fct_replace = array(
	'	".(($show[filter_closed_threads]) ? ("
			<td class=\"smallfont\" style=\"padding-$stylevar[right]:$stylevar[cellpadding]px\">
				<div><label for=\"sel_filterclosedthreads\">$vbphrase[closed_threads]</label></div>
				<select name=\"closed\" id=\"sel_filterclosedthreads\">
					<option value=\"hide\" $filterclosedthreadssel[hide]>$vbphrase[hide]</option>
					<option value=\"show\" $filterclosedthreadssel[show]>$vbphrase[show]</option>
				</select>
			</td>
			<td class=\"smallfont\">
	") : ("
			<td class=\"smallfont\" colspan=\"2\">
	"))."'
  );

$vbulletin->templatecache['FORUMDISPLAY'] = str_replace(
    $fct_search  ,
    $fct_replace ,
    $vbulletin->templatecache['FORUMDISPLAY']
  );

unset( $fct_search, $fct_replace );
